// Utilidades personalizadas para NihongoApp
(function(global) {
    'use strict';
    
    const NihongoUtils = {
        // Datos de Hiragana
        hiragana: ['あ', 'い', 'う', 'え', 'お', 'か', 'き', 'く', 'け', 'こ', 
                   'が', 'ぎ', 'ぐ', 'げ', 'ご', 'さ', 'し', 'す', 'せ', 'そ',
                   'ざ', 'じ', 'ず', 'ぜ', 'ぞ', 'た', 'ち', 'つ', 'て', 'と',
                   'だ', 'ぢ', 'づ', 'で', 'ど', 'な', 'に', 'ぬ', 'ね', 'の',
                   'は', 'ひ', 'ふ', 'へ', 'ほ', 'ば', 'び', 'ぶ', 'べ', 'ぼ',
                   'ぱ', 'ぴ', 'ぷ', 'ぺ', 'ぽ', 'ま', 'み', 'む', 'め', 'も',
                   'や', 'ゆ', 'よ', 'ら', 'り', 'る', 'れ', 'ろ',
                   'わ', 'ゐ', 'ゑ', 'を', 'ん'],
        
        // Datos de Katakana
        katakana: ['ア', 'イ', 'ウ', 'エ', 'オ', 'カ', 'キ', 'ク', 'ケ', 'コ',
                   'ガ', 'ギ', 'グ', 'ゲ', 'ゴ', 'サ', 'シ', 'ス', 'セ', 'ソ',
                   'ザ', 'ジ', 'ズ', 'ゼ', 'ゾ', 'タ', 'チ', 'ツ', 'テ', 'ト',
                   'ダ', 'ヂ', 'ヅ', 'デ', 'ド', 'ナ', 'ニ', 'ヌ', 'ネ', 'ノ',
                   'ハ', 'ヒ', 'フ', 'ヘ', 'ホ', 'バ', 'ビ', 'ブ', 'ベ', 'ボ',
                   'パ', 'ピ', 'プ', 'ペ', 'ポ', 'マ', 'ミ', 'ム', 'メ', 'モ',
                   'ヤ', 'ユ', 'ヨ', 'ラ', 'リ', 'ル', 'レ', 'ロ',
                   'ワ', 'ヰ', 'ヱ', 'ヲ', 'ン'],
        
        // Funciones de aprendizaje
        flipCard: function(cardElement) {
            if (cardElement) {
                cardElement.classList.toggle('flipped');
            }
        },
        
        getRandomIndex: function(arrayLength) {
            return Math.floor(Math.random() * arrayLength);
        },
        
        shuffleArray: function(array) {
            const shuffled = [...array];
            for (let i = shuffled.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
            }
            return shuffled;
        },
        
        // Datos de vocabulario básico
        vocabulary: [
            { japanese: 'こんにちは', romaji: 'konnichiwa', spanish: 'hola' },
            { japanese: 'ありがとう', romaji: 'arigatou', spanish: 'gracias' },
            { japanese: 'さようなら', romaji: 'sayounara', spanish: 'adiós' },
            { japanese: 'すみません', romaji: 'sumimasen', spanish: 'disculpa' },
            { japanese: 'おはよう', romaji: 'ohayou', spanish: 'buenos días' },
            { japanese: 'こんばんは', romaji: 'kombanwa', spanish: 'buenas tardes' },
            { japanese: 'わかりました', romaji: 'wakarimashita', spanish: 'entendí' },
            { japanese: 'はじめまして', romaji: 'hajimemashite', spanish: 'encantado' }
        ],
        
        // Sistema de progreso
        progress: {
            score: 0,
            attempts: 0,
            correct: 0,
            
            updateScore: function(points) {
                this.score += points;
                this.attempts++;
                if (points > 0) this.correct++;
            },
            
            getAccuracy: function() {
                return this.attempts > 0 ? Math.round((this.correct / this.attempts) * 100) : 0;
            },
            
            reset: function() {
                this.score = 0;
                this.attempts = 0;
                this.correct = 0;
            }
        }
    };
    
    if (typeof module !== 'undefined' && module.exports) {
        module.exports = NihongoUtils;
    } else {
        global.NihongoUtils = NihongoUtils;
    }
    
})(typeof window !== 'undefined' ? window : global);
